INSERT INTO testimonials (id, name, role, content, rating, featured, created_at) VALUES 
('cassio-silva', 'Cassio Silva Takarada', 'Peregrino Dedicado', 'A Caminhada do Perdão de Assis pra mim é mais que uma superação física. Além de me conectar mais com Deus e seu propósito, desde minha primeira Caminhada venho com a minha família, a qual é minha motivação para continuar persistindo na fé.', 5, 1, datetime('now')),
('teresa-silva', 'Teresa Oliveira Silva', 'Mãe Peregrина', 'Caminhar ao lado dos meus filhos no Caminho do Perdão foi transformador. Não apenas para mim, mas para toda minha família. Voltamos renovados e com uma conexão espiritual mais profunda.', 5, 0, datetime('now')),
('lucas-mendes', 'Lucas Mendes', 'Jovem Peregrino', 'Esperava apenas uma caminhada, mas recebi muito mais. O perdão que aprendi transformou meus relacionamentos e minha forma de ver a vida. Recomendo para todos que buscam paz interior.', 5, 0, datetime('now')),
('patricia-santos', 'Patrícia Santos Costa', 'Coordenadora Voluntária', 'Participei da organização e foi tão gratificante quanto caminhar. Ver pessoas se transformando e encontrando propósito é o maior presente. O Caminho do Perdão mudou minha vida para sempre.', 5, 0, datetime('now')),
('gabriel-ferreira', 'Gabriel Ferreira', 'Profissional em Transição', 'Carreguei muito peso das minhas mágoas. Nesta caminhada deixei muita coisa para trás e retornei leve, com esperança renovada e propósito claro.', 5, 1, datetime('now'));
